import 'package:flutter/material.dart';\nimport 'package:flutter/services.dart';\n\nclass EdgeToEdgeHelper {\n  static void enableEdgeToEdge() {\n    SystemChrome.setEnabledSystemUIMode(\n      SystemUiMode.edgeToEdge,\n      overlays: [SystemUiOverlay.top],\n    );\n    \n    SystemChrome.setSystemUIOverlayStyle(\n      const SystemUiOverlayStyle(\n        statusBarColor: Colors.transparent,\n        statusBarIconBrightness: Brightness.dark,\n        systemNavigationBarColor: Colors.transparent,\n        systemNavigationBarIconBrightness: Brightness.dark,\n      ),\n    );\n  }\n  \n  static void setSystemUIOverlayStyle({\n    required bool isDarkMode,\n    Color? statusBarColor,\n    Color? navigationBarColor,\n  }) {\n    SystemChrome.setSystemUIOverlayStyle(\n      SystemUiOverlayStyle(\n        statusBarColor: statusBarColor ?? Colors.transparent,\n        statusBarIconBrightness: isDarkMode ? Brightness.light : Brightness.dark,\n        systemNavigationBarColor: navigationBarColor ?? Colors.transparent,\n        systemNavigationBarIconBrightness: isDarkMode ? Brightness.light : Brightness.dark,\n      ),\n    );\n  }\n  \n  static Widget wrapWithSafeArea({\n    required Widget child,\n    bool top = true,\n    bool bottom = true,\n    bool left = true,\n    bool right = true,\n  }) {\n    return SafeArea(\n      top: top,\n      bottom: bottom,\n      left: left,\n      right: right,\n      child: child,\n    );\n  }\n  \n  static double getStatusBarHeight(BuildContext context) {\n    return MediaQuery.of(context).padding.top;\n  }\n  \n  static double getNavigationBarHeight(BuildContext context) {\n    return MediaQuery.of(context).padding.bottom;\n  }\n  \n  static bool hasNotch(BuildContext context) {\n    return MediaQuery.of(context).padding.top > 24;\n  }\n}