import 'package:get/get.dart';\n\nclass CareController extends GetxController with GetSingleTickerProviderStateMixin {\n  late TabController tabController;\n  \n  // Water Calculator\n  final potSizeController = TextEditingController();\n  final _selectedPlantType = 'Houseplant'.obs;\n  final _selectedSeason = 'Spring'.obs;\n  final _calculatedAmount = 0.0.obs;\n\n  final plantTypes = [\n    'Houseplant',\n    'Succulent',\n    'Tropical',\n    'Flowering',\n    'Herb',\n  ];\n\n  final seasons = [\n    'Spring',\n    'Summer',\n    'Fall',\n    'Winter',\n  ];\n\n  String get selectedPlantType => _selectedPlantType.value;\n  String get selectedSeason => _selectedSeason.value;\n  double get calculatedAmount => _calculatedAmount.value;\n\n  @override\n  void onInit() {\n    super.onInit();\n    tabController = TabController(length: 3, vsync: this);\n  }\n\n  @override\n  void onClose() {\n    tabController.dispose();\n    potSizeController.dispose();\n    super.onClose();\n  }\n\n  void setPlantType(String type) {\n    _selectedPlantType.value = type;\n  }\n\n  void setSeason(String season) {\n    _selectedSeason.value = season;\n  }\n\n  void calculateWater() {\n    final potSize = double.tryParse(potSizeController.text) ?? 0.0;\n    if (potSize > 0) {\n      // Simple calculation formula\n      double baseAmount = potSize * 0.1; // 10% of pot volume\n      \n      // Adjust for plant type\n      switch (_selectedPlantType.value) {\n        case 'Succulent':\n          baseAmount *= 0.5;\n          break;\n        case 'Tropical':\n          baseAmount *= 1.3;\n          break;\n        case 'Flowering':\n          baseAmount *= 1.2;\n          break;\n      }\n      \n      // Adjust for season\n      switch (_selectedSeason.value) {\n        case 'Summer':\n          baseAmount *= 1.4;\n          break;\n        case 'Winter':\n          baseAmount *= 0.7;\n          break;\n        case 'Fall':\n          baseAmount *= 0.8;\n          break;\n      }\n      \n      _calculatedAmount.value = baseAmount;\n    }\n  }\n}